// JavaScript Document

function allowDrop(ev) {
  ev.preventDefault();
}

function drag(ev) {
  ev.dataTransfer.setData("text", ev.target.id);
}

function drop(ev) {
  ev.preventDefault();
  var data = ev.dataTransfer.getData("text");
  ev.target.appendChild(document.getElementById(data));
}

let prev;

let bl = document.getElementById("blue");
bl.addEventListener("click", bluF, false);

function bluF(){
  if(prev !== bl){
    if(prev == gr || prev == red){
      prev.style.border = "5px solid white";
    }
    prev = bl;
    prev.className = "selected";
    prev.style.border = "5px solid blue";
  }

  else if(prev == bl){
    prev.style.border = "5px solid white";
    prev = "";
  }
}

